function ocultaElementosDoHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElementosDoHeader(){document.querySelector("header").classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`),a=document.querySelectorAll("[data-tab-id]");for(let e=0;e<a.length;e++)a[e].classList.remove("detalhes__list--is-active");t.classList.add("detalhes__list--is-active");var o=document.querySelectorAll("[data-tab-button]");for(let e=0;e<o.length;e++)o[e].classList.remove("detalhes__more__button--is-active");e.target.classList.add("detalhes__more__button--is-active")});const e=document.querySelector(".header").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaElementosDoHeader:exibeElementosDoHeader)()})});